const dataContainer = document.getElementById("data-container");
const btnMore = document.getElementById("btn_more");

// 6개의 상품을 추가하는 함수
function loadMoreProducts() {
    const productsToAdd = products.slice(productCount, productCount + 6);
    productsToAdd.forEach(product => {
        const productElement = document.createElement("p");
        productElement.textContent = product;
        dataContainer.appendChild(productElement);
    });

    productCount += 6;

    // 모든 상품이 추가되었으면 버튼을 숨김
    if (productCount >= products.length) {
        btnMore.style.display = "none";
    }
}

// "더 보기" 버튼 클릭 시 6개의 상품을 추가
btnMore.addEventListener("click", loadMoreProducts);